(function(){document.addEventListener("DOMContentLoaded",initAbout);var about=document.querySelector(".about");var aboutButton=document.querySelector(".about-button");var header=document.querySelector(".header");var main=document.querySelector(".main");var bodyModifierClass="body--unscrollable";var headerModifierClass="header--pushed";var aboutTransitionDuration=about&&parseFloat(window.getComputedStyle(about)["transitionDuration"])*1e3;function initAbout(){aboutButton&&aboutButton.addEventListener("click",toggleAbout)}function isAboutExpanded(){return about.getAttribute("aria-expanded")==="true"}function changeAriaStates(state){aboutButton.setAttribute("aria-pressed",state);about.setAttribute("aria-expanded",state);main.setAttribute("aria-hidden",state);var timeout=state?0:aboutTransitionDuration;setTimeout(function(){about.setAttribute("aria-hidden",!state)},timeout)}function toggleClasses(){header.classList.toggle(headerModifierClass);document.body.classList.toggle(bodyModifierClass)}function toggleAbout(){toggleClasses();changeAriaStates(!isAboutExpanded())}})();
